mapper.select:
        SELECT
        
        

        FROM pandian  a

        
            
                and (
                a.admin_id = #{params.adminId}
                )
            
            
                and a.id in
                
                #{item}
                
            
            
                and a.pandian_content like CONCAT('%',#{params.pandianContent},'%')
            

        
        order by a.${params.sort} ${params.order}
